import { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import './Home.css';

const API_URL = 'http://localhost:5001/api';

const Home = () => {
  const { user, logout, updateUser } = useAuth();
  const [keyword, setKeyword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [tracks, setTracks] = useState([]);
  const [trendingTracks, setTrendingTracks] = useState([]);
  const [favorites, setFavorites] = useState(user?.favorites || []);
  const [playlists, setPlaylists] = useState(user?.playlists || []);
  const [activeTab, setActiveTab] = useState('trending');
  const [showPlaylistModal, setShowPlaylistModal] = useState(false);
  const [selectedTrack, setSelectedTrack] = useState(null);
  const [newPlaylistName, setNewPlaylistName] = useState('');
  const [showCreatePlaylist, setShowCreatePlaylist] = useState(false);
  const [currentlyPlaying, setCurrentlyPlaying] = useState(null);
  const [modalNewPlaylistName, setModalNewPlaylistName] = useState('');
  const [showModalCreatePlaylist, setShowModalCreatePlaylist] = useState(false);
  const [addedMessage, setAddedMessage] = useState('');

  // Load trending songs on mount
  useEffect(() => {
    loadTrendingSongs();
  }, []);

  useEffect(() => {
    if (user) {
      setFavorites(user.favorites || []);
      setPlaylists(user.playlists || []);
    }
  }, [user]);

  const loadTrendingSongs = async () => {
    setIsLoading(true);
    try {
      let data = await fetch(
        `https://v1.nocodeapi.com/bhawana_ydv/spotify/fxxAcPsnsMBcOXtN/search?q=trending%202024&type=track&limit=20`
      );
      let convertedData = await data.json();
      setTrendingTracks(convertedData.tracks?.items?.slice(0, 20) || []);
    } catch (error) {
      console.error('Trending songs error:', error);
    }
    setIsLoading(false);
  };

  const getTracks = async () => {
    if (!keyword.trim()) return;
    setIsLoading(true);
    setActiveTab('search');
    try {
      let data = await fetch(
        `https://v1.nocodeapi.com/bhawana_ydv/spotify/fxxAcPsnsMBcOXtN/search?q=${keyword}&type=track&limit=20`
      );
      let convertedData = await data.json();
      setTracks(convertedData.tracks?.items || []);
    } catch (error) {
      console.error('Search error:', error);
    }
    setIsLoading(false);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      getTracks();
    }
  };

  const addToFavorites = async (track) => {
    try {
      const res = await axios.post(`${API_URL}/user/favorites`, {
        trackId: track.id,
        trackName: track.name,
        artistName: track.artists?.[0]?.name || 'Unknown',
        albumImage: track.album?.images?.[0]?.url || '',
        previewUrl: track.preview_url || ''
      });
      setFavorites(res.data.favorites);
      updateUser({ ...user, favorites: res.data.favorites });
      showMessage('Added to favorites! â¤ï¸');
    } catch (error) {
      console.error('Add favorite error:', error);
    }
  };

  const removeFromFavorites = async (trackId) => {
    try {
      const res = await axios.delete(`${API_URL}/user/favorites/${trackId}`);
      setFavorites(res.data.favorites);
      updateUser({ ...user, favorites: res.data.favorites });
      showMessage('Removed from favorites');
    } catch (error) {
      console.error('Remove favorite error:', error);
    }
  };

  const isFavorite = (trackId) => {
    return favorites.some(f => f.trackId === trackId);
  };

  const showMessage = (msg) => {
    setAddedMessage(msg);
    setTimeout(() => setAddedMessage(''), 2000);
  };

  const createPlaylist = async () => {
    if (!newPlaylistName.trim()) return;
    try {
      const res = await axios.post(`${API_URL}/user/playlists`, {
        name: newPlaylistName,
        description: ''
      });
      setPlaylists(res.data.playlists);
      updateUser({ ...user, playlists: res.data.playlists });
      setNewPlaylistName('');
      setShowCreatePlaylist(false);
      showMessage('Playlist created! ðŸ“');
    } catch (error) {
      console.error('Create playlist error:', error);
    }
  };

  const createPlaylistFromModal = async () => {
    if (!modalNewPlaylistName.trim()) return;
    try {
      const res = await axios.post(`${API_URL}/user/playlists`, {
        name: modalNewPlaylistName,
        description: ''
      });
      const newPlaylists = res.data.playlists;
      setPlaylists(newPlaylists);
      updateUser({ ...user, playlists: newPlaylists });

      // Automatically add the selected track to the new playlist
      const newPlaylist = newPlaylists[newPlaylists.length - 1];
      await addToPlaylist(newPlaylist._id, selectedTrack);

      setModalNewPlaylistName('');
      setShowModalCreatePlaylist(false);
    } catch (error) {
      console.error('Create playlist error:', error);
    }
  };

  const deletePlaylist = async (playlistId) => {
    if (!window.confirm('Are you sure you want to delete this playlist?')) return;
    try {
      const res = await axios.delete(`${API_URL}/user/playlists/${playlistId}`);
      setPlaylists(res.data.playlists);
      updateUser({ ...user, playlists: res.data.playlists });
      showMessage('Playlist deleted');
    } catch (error) {
      console.error('Delete playlist error:', error);
    }
  };

  const addToPlaylist = async (playlistId, track) => {
    try {
      const trackData = track.trackId ? track : {
        trackId: track.id,
        trackName: track.name,
        artistName: track.artists?.[0]?.name || track.artistName || 'Unknown',
        albumImage: track.album?.images?.[0]?.url || track.albumImage || '',
        previewUrl: track.preview_url || track.previewUrl || ''
      };

      const res = await axios.post(`${API_URL}/user/playlists/${playlistId}/tracks`, trackData);
      setPlaylists(prev => prev.map(p => p._id === playlistId ? res.data.playlist : p));
      updateUser({ ...user, playlists: playlists.map(p => p._id === playlistId ? res.data.playlist : p) });
      setShowPlaylistModal(false);
      setSelectedTrack(null);
      showMessage('Added to playlist! ðŸŽµ');
    } catch (error) {
      if (error.response?.data?.message === 'Track already in playlist') {
        showMessage('Track already in this playlist');
      } else {
        console.error('Add to playlist error:', error);
      }
    }
  };

  const removeFromPlaylist = async (playlistId, trackId) => {
    try {
      const res = await axios.delete(`${API_URL}/user/playlists/${playlistId}/tracks/${trackId}`);
      setPlaylists(prev => prev.map(p => p._id === playlistId ? res.data.playlist : p));
      updateUser({ ...user, playlists: playlists.map(p => p._id === playlistId ? res.data.playlist : p) });
      showMessage('Removed from playlist');
    } catch (error) {
      console.error('Remove from playlist error:', error);
    }
  };

  const openPlaylistModal = (track) => {
    setSelectedTrack(track);
    setShowPlaylistModal(true);
    setShowModalCreatePlaylist(false);
    setModalNewPlaylistName('');
  };

  const TrackCard = ({ track, isFromSearch = true, playlistId = null }) => {
    const trackId = isFromSearch ? track.id : track.trackId;
    const trackName = isFromSearch ? track.name : track.trackName;
    const artistName = isFromSearch ? (track.artists?.[0]?.name || 'Unknown') : track.artistName;
    const albumImage = isFromSearch ? (track.album?.images?.[0]?.url || '') : track.albumImage;
    const previewUrl = isFromSearch ? track.preview_url : track.previewUrl;

    return (
      <div className="track-card">
        <div className="track-image">
          {albumImage ? (
            <img src={albumImage} alt={trackName} />
          ) : (
            <div className="no-image">ðŸŽµ</div>
          )}
          {previewUrl && (
            <button 
              className={`play-button ${currentlyPlaying === trackId ? 'playing' : ''}`}
              onClick={() => setCurrentlyPlaying(currentlyPlaying === trackId ? null : trackId)}
            >
              {currentlyPlaying === trackId ? 'â¸' : 'â–¶'}
            </button>
          )}
        </div>
        <div className="track-info">
          <h3 className="track-name">{trackName}</h3>
          <p className="artist-name">{artistName}</p>
        </div>
        <div className="track-actions">
          {isFromSearch ? (
            <>
              <button 
                className={`action-btn favorite-btn ${isFavorite(trackId) ? 'active' : ''}`}
                onClick={() => isFavorite(trackId) ? removeFromFavorites(trackId) : addToFavorites(track)}
                title={isFavorite(trackId) ? 'Remove from favorites' : 'Add to favorites'}
              >
                {isFavorite(trackId) ? 'â¤ï¸' : 'ðŸ¤'}
              </button>
              <button 
                className="action-btn playlist-btn"
                onClick={() => openPlaylistModal(track)}
                title="Add to playlist"
              >
                âž•
              </button>
            </>
          ) : playlistId ? (
            <button 
              className="action-btn remove-btn"
              onClick={() => removeFromPlaylist(playlistId, trackId)}
              title="Remove from playlist"
            >
              âœ•
            </button>
          ) : (
            <>
              <button 
                className="action-btn playlist-btn"
                onClick={() => openPlaylistModal(track)}
                title="Add to playlist"
              >
                âž•
              </button>
              <button 
                className="action-btn remove-btn"
                onClick={() => removeFromFavorites(trackId)}
                title="Remove from favorites"
              >
                âœ•
              </button>
            </>
          )}
        </div>
        {currentlyPlaying === trackId && previewUrl && (
          <audio 
            src={previewUrl} 
            autoPlay 
            onEnded={() => setCurrentlyPlaying(null)}
            className="hidden-audio"
          />
        )}
      </div>
    );
  };

  return (
    <div className="home-container">
      {/* Toast Message */}
      {addedMessage && (
        <div className="toast-message">
          {addedMessage}
        </div>
      )}

      {/* Navbar */}
      <nav className="navbar">
        <div className="navbar-brand">
          <span className="brand-icon">ðŸŽµ</span>
          <span className="brand-text">V-Music</span>
        </div>

        <div className="search-bar">
          <input
            type="text"
            value={keyword}
            onChange={(e) => setKeyword(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Search for tracks..."
          />
          <button onClick={getTracks} className="search-btn">
            ðŸ”
          </button>
        </div>

        <div className="navbar-user">
          <span className="user-greeting">Hi, {user?.name?.split(' ')[0]}</span>
          <button onClick={logout} className="logout-btn">Logout</button>
        </div>
      </nav>

      {/* Tabs */}
      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'trending' ? 'active' : ''}`}
          onClick={() => setActiveTab('trending')}
        >
          ðŸ”¥ Trending
        </button>
        <button 
          className={`tab ${activeTab === 'search' ? 'active' : ''}`}
          onClick={() => setActiveTab('search')}
        >
          ðŸ” Search
        </button>
        <button 
          className={`tab ${activeTab === 'favorites' ? 'active' : ''}`}
          onClick={() => setActiveTab('favorites')}
        >
          â¤ï¸ Favorites ({favorites.length})
        </button>
        <button 
          className={`tab ${activeTab === 'playlists' ? 'active' : ''}`}
          onClick={() => setActiveTab('playlists')}
        >
          ðŸ“ Playlists ({playlists.length})
        </button>
      </div>

      {/* Main Content */}
      <main className="main-content">
        {/* Trending Tab */}
        {activeTab === 'trending' && (
          <div className="trending-section">
            <div className="section-header">
              <h2>ðŸ”¥ Top 20 Trending Songs</h2>
              <button onClick={loadTrendingSongs} className="refresh-btn" disabled={isLoading}>
                {isLoading ? 'â³' : 'ðŸ”„'} Refresh
              </button>
            </div>
            {isLoading ? (
              <div className="loading-state">
                <div className="spinner"></div>
                <p>Loading trending songs...</p>
              </div>
            ) : trendingTracks.length > 0 ? (
              <div className="tracks-grid">
                {trendingTracks.map((track, index) => (
                  <div key={track.id} className="track-wrapper">
                    <span className="track-rank">#{index + 1}</span>
                    <TrackCard track={track} isFromSearch={true} />
                  </div>
                ))}
              </div>
            ) : (
              <div className="empty-state">
                <span className="empty-icon">ðŸŽ¶</span>
                <h2>Unable to load trending songs</h2>
                <p>Click refresh to try again</p>
              </div>
            )}
          </div>
        )}

        {/* Search Tab */}
        {activeTab === 'search' && (
          <div className="search-section">
            {isLoading ? (
              <div className="loading-state">
                <div className="spinner"></div>
                <p>Searching for tracks...</p>
              </div>
            ) : tracks.length > 0 ? (
              <>
                <div className="section-header">
                  <h2>Search Results for "{keyword}"</h2>
                </div>
                <div className="tracks-grid">
                  {tracks.map(track => (
                    <TrackCard key={track.id} track={track} isFromSearch={true} />
                  ))}
                </div>
              </>
            ) : (
              <div className="empty-state">
                <span className="empty-icon">ðŸ”</span>
                <h2>Search for your favorite music</h2>
                <p>Type in the search bar and press Enter or click the search button</p>
              </div>
            )}
          </div>
        )}

        {/* Favorites Tab */}
        {activeTab === 'favorites' && (
          <div className="favorites-section">
            <div className="section-header">
              <h2>â¤ï¸ Your Favorites</h2>
            </div>
            {favorites.length > 0 ? (
              <div className="tracks-grid">
                {favorites.map(track => (
                  <TrackCard key={track.trackId} track={track} isFromSearch={false} />
                ))}
              </div>
            ) : (
              <div className="empty-state">
                <span className="empty-icon">â¤ï¸</span>
                <h2>No favorites yet</h2>
                <p>Like songs to add them here</p>
              </div>
            )}
          </div>
        )}

        {/* Playlists Tab */}
        {activeTab === 'playlists' && (
          <div className="playlists-section">
            <div className="section-header">
              <h2>ðŸ“ Your Playlists</h2>
              <button 
                className="create-playlist-btn"
                onClick={() => setShowCreatePlaylist(true)}
              >
                âž• Create Playlist
              </button>
            </div>

            {showCreatePlaylist && (
              <div className="create-playlist-form">
                <input
                  type="text"
                  value={newPlaylistName}
                  onChange={(e) => setNewPlaylistName(e.target.value)}
                  placeholder="Enter playlist name..."
                  onKeyPress={(e) => e.key === 'Enter' && createPlaylist()}
                  autoFocus
                />
                <button onClick={createPlaylist} className="save-btn">Create</button>
                <button onClick={() => setShowCreatePlaylist(false)} className="cancel-btn">Cancel</button>
              </div>
            )}

            {playlists.length > 0 ? (
              <div className="playlists-list">
                {playlists.map(playlist => (
                  <div key={playlist._id} className="playlist-item">
                    <div className="playlist-header">
                      <div className="playlist-info">
                        <h3>ðŸ“ {playlist.name}</h3>
                        <span className="track-count">{playlist.tracks?.length || 0} tracks</span>
                      </div>
                      <button 
                        className="delete-playlist-btn"
                        onClick={() => deletePlaylist(playlist._id)}
                        title="Delete playlist"
                      >
                        ðŸ—‘ï¸
                      </button>
                    </div>
                    {playlist.tracks?.length > 0 ? (
                      <div className="playlist-tracks">
                        {playlist.tracks.map(track => (
                          <TrackCard 
                            key={track.trackId} 
                            track={track} 
                            isFromSearch={false}
                            playlistId={playlist._id}
                          />
                        ))}
                      </div>
                    ) : (
                      <p className="empty-playlist">No tracks in this playlist. Search for songs and add them!</p>
                    )}
                  </div>
                ))}
              </div>
            ) : (
              <div className="empty-state">
                <span className="empty-icon">ðŸ“</span>
                <h2>No playlists yet</h2>
                <p>Create a playlist to organize your music</p>
              </div>
            )}
          </div>
        )}
      </main>

      {/* Add to Playlist Modal */}
      {showPlaylistModal && (
        <div className="modal-overlay" onClick={() => setShowPlaylistModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>âž• Add to Playlist</h3>
              <button className="modal-close-x" onClick={() => setShowPlaylistModal(false)}>âœ•</button>
            </div>

            {selectedTrack && (
              <div className="modal-track-preview">
                <img 
                  src={selectedTrack.album?.images?.[0]?.url || selectedTrack.albumImage || ''} 
                  alt={selectedTrack.name || selectedTrack.trackName}
                />
                <div>
                  <p className="preview-track-name">{selectedTrack.name || selectedTrack.trackName}</p>
                  <p className="preview-artist-name">{selectedTrack.artists?.[0]?.name || selectedTrack.artistName}</p>
                </div>
              </div>
            )}

            <div className="modal-content">
              {playlists.length > 0 && (
                <>
                  <p className="modal-subtitle">Choose a playlist:</p>
                  <div className="playlist-options">
                    {playlists.map(playlist => (
                      <button
                        key={playlist._id}
                        className="playlist-option"
                        onClick={() => addToPlaylist(playlist._id, selectedTrack)}
                      >
                        <span className="option-icon">ðŸ“</span>
                        <span className="option-name">{playlist.name}</span>
                        <span className="option-count">{playlist.tracks?.length || 0} tracks</span>
                      </button>
                    ))}
                  </div>
                  <div className="modal-divider">
                    <span>or</span>
                  </div>
                </>
              )}

              {showModalCreatePlaylist ? (
                <div className="modal-create-playlist">
                  <input
                    type="text"
                    value={modalNewPlaylistName}
                    onChange={(e) => setModalNewPlaylistName(e.target.value)}
                    placeholder="Enter playlist name..."
                    onKeyPress={(e) => e.key === 'Enter' && createPlaylistFromModal()}
                    autoFocus
                  />
                  <div className="modal-create-actions">
                    <button onClick={createPlaylistFromModal} className="save-btn">Create & Add</button>
                    <button onClick={() => setShowModalCreatePlaylist(false)} className="cancel-btn">Cancel</button>
                  </div>
                </div>
              ) : (
                <button 
                  className="create-new-playlist-btn"
                  onClick={() => setShowModalCreatePlaylist(true)}
                >
                  âž• Create New Playlist
                </button>
              )}
            </div>

            <button className="modal-close" onClick={() => setShowPlaylistModal(false)}>
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
